import React, { Component } from "react";
import "./style.css";
import Axios from "axios";

class ResultsCard extends Component {
    state = {
        favorite: false
    };

    handleFavoriteClick = event => {
        event.preventDefault();

        console.log(event.target.getAttribute("data-key"));
        this.setState({
            favorite: true
        });

        Axios.post("/api/favorite", {
            favorite: this.state.favorite
        })
            .catch(function (error) {
                console.log(error);
            });
    }



    render() {
        return (
            <div>
                {
                    this.props.searchResults.map(result => {
                        return (
                            <div>
                                <div className="card item">
                                    <div className="img-container">
                                        <img alt={result.name} src={result.primary_photo_cropped} />
                                    </div>
                                    <button className="favoriteBtn" onClick={this.handleFavoriteClick} data-key={result.id}>
                                        <img src="https://img.icons8.com/nolan/50/likes-folder.png" data-key={result.id}/>
                                    </button>
                                    <div className="content">
                                        <ul>
                                            <li>
                                                <strong>Name:</strong> {result.name}
                                            </li>
                                            <li>
                                                <strong>Age:</strong> {result.age}
                                            </li>
                                            <li>
                                                <strong>Description:</strong> {result.description}
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        );
                    })
                }
            </div>
        );
    }
}

export default ResultsCard;




